opts-matchKeysStrictly.js.content:
import &#x7B; strict as assert &#x7D; from "assert";
import deleteObj from "ast-delete-object";

const source = [
  "elem1",
  &#x7B;
    findme1: "zzz",
    findme2: "yyy",
    somethingelse: "qqq", // <--- this key will block deletion
  &#x7D;,
  "elem2",
];

// nothing happens - matching was strict
assert.deepEqual(
  deleteObj(
    source,
    &#x7B;
      findme1: "zzz",
      findme2: "yyy",
    &#x7D;,
    &#x7B;
      matchKeysStrictly: true, // <--- strict matching
    &#x7D;
  ),
  source
);

// but
assert.deepEqual(
  deleteObj(
    source,
    &#x7B;
      findme1: "zzz",
      findme2: "yyy",
    &#x7D;,
    &#x7B;
      matchKeysStrictly: false,
    &#x7D;
  ),
  ["elem1", "elem2"]
);

