real-use.js.content:
import &#x7B; strict as assert &#x7D; from "assert";
import getAllValuesByKey from "ast-get-values-by-key";

const source = &#x7B;
  orders: [
    &#x7B;
      date: "2020-09-26",
      amount: null, // replace
      product: "Bramble",
    &#x7D;,
    &#x7B;
      date: "2020-09-26",
      amount: 2,
      product: "Croodle",
    &#x7D;,
    &#x7B;
      date: "2020-09-26",
      amount: null, // replace
      product: "Zapple",
    &#x7D;,
  ],
&#x7D;;

// first GET
const findings = getAllValuesByKey(
  source, // what to process
  "amount" // what to look for
);
assert.deepEqual(findings, [
  &#x7B; val: null, path: "orders.0.amount" &#x7D;,
  &#x7B; val: 2, path: "orders.1.amount" &#x7D;,
  &#x7B; val: null, path: "orders.2.amount" &#x7D;,
]);

// prepare replacement array for SET third input argument
const replacement = findings.map((&#x7B; val &#x7D;) => +val);
assert.deepEqual(replacement, [0, 2, 0]);

// then SET
const result = getAllValuesByKey(
  source, // what to process
  "amount", // what to look for
  replacement // pot of replacement values to consume (single-use, FIFO stack)
);
assert.deepEqual(result, &#x7B;
  orders: [
    &#x7B;
      date: "2020-09-26",
      amount: 0, // replace
      product: "Bramble",
    &#x7D;,
    &#x7B;
      date: "2020-09-26",
      amount: 2,
      product: "Croodle",
    &#x7D;,
    &#x7B;
      date: "2020-09-26",
      amount: 0, // replace
      product: "Zapple",
    &#x7D;,
  ],
&#x7D;);

