  const current = val1 !== undefined ? val1 : key1;
  result1.push(innerObj.path);
  return current;
&#x7D;);

// notice the object-path notation is "b.0.c" not "b[0].c"
assert.deepEqual(result1, ["a", "b", "b.0", "b.0.c"]);

