import fillMissing from "object-fill-missing-keys";

assert.deepEqual(
  fillMissing(
    &#x7B;
      // input object
      a: &#x7B;
        b: &#x7B;
          // this object in "b"'s value will be removed and set to placeholder "false"
          c: false,
          d: false,
        &#x7D;,
        x: &#x7B;
          // this too
          y: false,
        &#x7D;,
      &#x7D;,
      z: "z",
    &#x7D;,
    &#x7B;
      // schema object
      a: &#x7B;
        b: &#x7B;
          c: false,
          d: false,
        &#x7D;,
        x: false,
      &#x7D;,
      z: false,
    &#x7D;,
    &#x7B;
      // settings
      doNotFillThesePathsIfTheyContainPlaceholders: ["lalala", "a.b", "a.x"],
    &#x7D;
  ),
  &#x7B;
    // result
    a: &#x7B;
      b: false,
      x: false,
    &#x7D;,
    z: "z",
  &#x7D;
);

