import &#x7B; strict as assert &#x7D; from "assert";
import fillMissing from "object-fill-missing-keys";

// on
assert.deepEqual(
  fillMissing(
    &#x7B;
      // object we're working on
      a: null,
    &#x7D;,
    &#x7B;
      // reference schema
      a: ["z"],
    &#x7D;,
    &#x7B;
      // options
      useNullAsExplicitFalse: true, // <--- !
    &#x7D;
  ),
  &#x7B;
    // result
    a: null,
  &#x7D;
);

// off
assert.deepEqual(
  fillMissing(
    &#x7B;
      // object we're working on
      a: null,
    &#x7D;,
    &#x7B;
      // reference schema
      a: ["z"],
    &#x7D;,
    &#x7B;
      // options
      useNullAsExplicitFalse: false, // <--- !
    &#x7D;
  ),
  &#x7B;
    // result
    a: ["z"],
  &#x7D;
);

