import &#x7B; strict as assert &#x7D; from "assert";
import iterate from "ranges-iterate";

// Ranges in the following example "punches out" a "hole" from `a` to `g`
// (included), replacing it with `xyz`. That's what gets iterated.

const gathered = [];

// a callback-based interface:
iterate("abcdefghij", [[0, 7, "xyz"]], (&#x7B; i, val &#x7D;) => &#x7B;
  gathered.push(`i = $&#x7B;i&#x7D;; val = $&#x7B;val&#x7D;`);
&#x7D;);

assert.deepEqual(gathered, [
  "i = 0; val = x",
  "i = 1; val = y",
  "i = 2; val = z",
  "i = 3; val = h",
  "i = 4; val = i",
  "i = 5; val = j",
]);

import &#x7B; strict as assert &#x7D; from "assert";
import mergeR from "ranges-merge";

// joining edges:
assert.deepEqual(
  mergeR([
    [1, 2],
    [2, 3],
    [9, 10],
  ]),
  [
    [1, 3],
    [9, 10],
  ]
);

// an overlap:
assert.deepEqual(
  mergeR([
    [1, 5],
    [2, 10],
  ]),
  [[1, 10]]
);

import &#x7B; strict as assert &#x7D; from "assert";
import processOutside from "ranges-process-outside";

const gathered = [];

// a callback interface:
processOutside(
  "abcdefghij",
  [
    [1, 5], // delete from "b" to "f"
  ],
  (fromIdx, toIdx, offsetValueCb) => &#x7B;
    gathered.push(fromIdx);
  &#x7D;
);

assert.deepEqual(gathered, [0, 5, 6, 7, 8, 9]);

import &#x7B; strict as assert &#x7D; from "assert";
import Ranges from "ranges-push";
import applyR from "ranges-apply";

const gatheredRanges = new Ranges();

const oldString = `The quick brown fox jumps over the lazy dog.`;

// push the ranges
gatheredRanges.push(35, 43, "little Red Riding Hood");
gatheredRanges.push(4, 19, "bad grey wolf");

// retrieve the merged and sorted ranges by calling .current()
assert.deepEqual(gatheredRanges.current(), [
  [4, 19, "bad grey wolf"],
  [35, 43, "little Red Riding Hood"],
]);

assert.equal(
  applyR(oldString, gatheredRanges.current()),
  "The bad grey wolf jumps over the little Red Riding Hood."
);

// wipe all gathered ranges
gatheredRanges.wipe();
assert.equal(gatheredRanges.current(), null);

import &#x7B; strict as assert &#x7D; from "assert";
import raReg from "ranges-regex";

const oldString = `The quick brown fox jumps over the lazy dog.`;
const result = raReg(/the/gi, oldString);

// all regex matches, but in Ranges notation (see codsen.com/ranges/):
assert.deepEqual(result, [
  [0, 3],
  [31, 34],
]);

// if you slice the ranges, you'll get original regex caught values:
assert.deepEqual(
  result.map(([from, to]) => oldString.slice(from, to)),
  ["The", "the"]
);

clashing-replacements.js.content:
import &#x7B; strict as assert &#x7D; from "assert";
import raReg from "ranges-regex";
import apply from "ranges-apply";
import Ranges from "ranges-push";

// Two tasks:
// 1. replace numbers with asterisks (*)
// 2. but delete all zero chunks

// tasks clash

const source = "sequence: 1234 5678 0000 1234";
const gatheredRanges = new Ranges();
// now can push() new ranges into "gatheredRanges"

// 1.

// conceal number chunks:
const fix1 = raReg(/\d/g, source, "*");
// \d matches digit
assert.deepEqual(fix1, [
  [10, 14, "****"],

  [15, 19, "****"],

  [20, 24, "****"],

  [25, 29, "****"],
]);
// replacement is correct:
assert.equal(apply(source, fix1), "sequence: **** **** **** ****");
// push it in:
gatheredRanges.push(fix1);

// 2.

// delete zero chunks:
const fix2 = raReg(/\b[0]+\b/g, source);
assert.deepEqual(fix2, [[20, 24]]);
// deletion is correct:
assert.equal(apply(source, fix2), "sequence: 1234 5678  1234");
// push it in:
gatheredRanges.push(fix2);

// 3. - MERGE and APPLY

// what have we got in "gatheredRanges"?
assert.deepEqual(gatheredRanges.current(), [
  [10, 14, "****"],
  [15, 19, "****"],
  [20, 24, "****"],
  [25, 29, "****"],
]);
// notice [[19, 23, "brown"]] is absent - "ranges-merge" program detected
// we're deleting an encompassing range of indexes [12, 29] which
// encompasses to-be-inserted values range at [19, 23] and discarded the
// latter.

// apply both fixes in one go:
assert.equal(
  apply(source, gatheredRanges.current()),
  "sequence: **** **** **** ****"
);

// hey, the zero chunk deletion range [20, 24] was merged with
// replacement range [20, 24, "****"] and former got lost!

// How do we make that the deletion would make precedence over everything
// else?

// Answer is, put `null` in replacement. It will be interpreted as explicit
// deletion.

// insert null as replacement:
const fix3 = raReg(/\b[0]+\b/g, source, null);
assert.deepEqual(fix3, [[20, 24, null]]);
// deletion is correct:
assert.equal(apply(source, fix3), "sequence: 1234 5678  1234");

// wipe the "gatheredRanges"
gatheredRanges.wipe();

// push again
gatheredRanges.push(fix1); // asterisks
gatheredRanges.push(fix3); // zero chunk

// apply again:
assert.equal(
  apply(source, gatheredRanges.current()),
  "sequence: **** ****  ****"
);
// null in [20, 24, null] overrode the insertion instruction [20, 24, "****"]

